<div class="b-template mt-4 p-6 bg-white border border-gray-200 rounded-lg shadow">
    <div class=" p-2" style="background-color: rgb(143, 229, 201);">
        <div style="  background-color: rgb(81, 184, 151);">
            <div class="tblCellMain" style="padding: 15px 0px;">
                <div class="tblLine" style="border-top-width: 0px; border-top-style: none; border-top-color: rgb(225, 225, 225); min-width: 600px;" width="100%" cellspacing="0" cellpadding="0" border="0"></div>
            </div>
            <div class="flex">
                <div class="bmeImage" style="padding: 0px; width:40%;" align="center">
                    <img src="https://www.benchmarkemail.com/images/templates_n/new_editor/Templates/Ornaments/Ornaments.jpg" style="height:100%; max-width: 600px; display: block;" alt="" width="100%">
                </div>
                <div style="width:60%; color: rgb(56, 56, 56); border: 0px none transparent;" >
                    <div id="dv_3" class="blk_wrapper">
                        <div class="tblCellMain" style="padding: 5px 20px;">
                            <div class="tblLine" style="border-top-width: 0px; border-top-style: none; border-top-color: rgb(225, 225, 225); min-width: 260px;" width="100%"></div>
                        </div>
                    </div>
                    <div id="dv_4" class="blk_wrapper">
                        <div name="tblCell" style="padding: 10px 20px; font-family: Arial, Helvetica, sans-serif; font-size: 14px; font-weight: normal; line-height: 150%; color: rgb(56, 56, 56); text-align: left;">
                            <div style="line-height: 100%;">
                                <em>
                                    <span style="font-size: 36px; font-family: Palatino, 'Book Antiqua', 'Times New Roman', Times, serif; color: #ffffff; line-height: 100%;">Season's</span>
                                </em>
                                <br>
                                <strong>
                    <span style="font-size: 60px; line-height: 100%;">
                      <em>
                        <span style="font-family: Palatino, 'Book Antiqua', 'Times New Roman', Times, serif; color: #ffffff; line-height: 100%;">Greetings</span>
                      </em>
                    </span>
                                </strong>
                            </div>
                        </div>
                    </div>
                    <div id="dv_9" class="blk_wrapper">
                        <div name="tblCell" style="padding: 20px; font-family: Arial, Helvetica, sans-serif; font-size: 14px; font-weight: normal; line-height: 150%; color: rgb(56, 56, 56); text-align: left;" valign="top" align="left">
                            <div style="line-height: 150%;">
                  <span style="font-size: 14px; font-family: Palatino, 'Book Antiqua', 'Times New Roman', Times, serif; color: #ffffff; line-height: 150%;">{{ $subject ?? 'No subject' }}<br>
                    <br>{{ $body ?? 'No body' }} <br>
                    <br>Sincerely,{{ $signature ?? 'No signature' }} <br>Company Team </span>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </div>

